let assert = require("assert");
let Phrase = require("../index.js");

describe("Phrase", function() {
  describe("#palindrome", function() {
    it("should return false for a non-palindrome", function() {
      let notAPalindrome = new Phrase("apple");
      assert(!notAPalindrome.palindrome());
    });

    it("should return true for a plain palindrome", function() {
      let plainPalindrome = new Phrase("racecar");
      assert(plainPalindrome.palindrome());
    });

    it("should return true for a mixed case palindrome", function() {
      let mixedCasePalindrome = new Phrase("Kayak");
      assert(mixedCasePalindrome.palindrome());
    });

    it("should return true for a punctuated palindrome", function() {
      let punctuatedPalindrome = new Phrase("A man, a plan, a canal: Panama!");
      assert(punctuatedPalindrome.palindrome());
    });
  });
  describe("letters", function() {
    it("should return only letters, no punctuation or whitespace", function() {
      let punctuatedPalindrome = new Phrase("A man, a plan, a canal: Panama!");
      assert.strictEqual(punctuatedPalindrome.letters(), "AmanaplanacanalPanama");
      // assert(punctuatedPalindrome.letters() === "AmanaplanacanalPanama");
    });
    it("should return empty string when there are no letters", function() {
      let characterLessPhrase = new Phrase("\"!@#$%^ &45;',.<>[]}{ 6789*()");
      assert.strictEqual(characterLessPhrase.letters(), '');
    });
  });
});
